import prisma from "../dbClient.js";
import { getTasks as getTasksUser } from "../../ext-src/queries.js";

export type GetTasks = typeof getTasks;

export const getTasks = async (args, context) => {
  return (getTasksUser as any)(args, {
    ...context,
    entities: {
      Task: prisma.task,
    },
  });
};
